import {
  routes
} from "./routes";
import {
  Switch,
  Route,
  BrowserRouter as Router
} from "react-router-dom";

const App = () => {

  const list_routes = () => {
    let result = routes.map((route, index) => {
      <Route
        key={index}
        path={route.path}
        exact={route.exact}
        component={route.components}
      />
      return (
        <Router>
            <Switch>{result}</Switch>
            <ToastContainer hideProgressBar autoClose={3000} />
        </Router>
      );
    })
  }

  return (
    list_routes ()
  )


}

export default App;
